// define functions
val ff = (x:Int) => {x + 1}
val fg = (x:Int) => {x * 2}

val ffComposeFg = ff compose fg    //compose function, ff is a function, no need for _

val ls10 = ls map ffComposeFg 

// always use val for defining function!!! 

val fncs = List(ff, fg)                                 //transformation list
val ls12 = ls map ( fncs.reverse reduce (_ compose _) ) //andThen
val ls13 = ls map ( fncs reduce (_ andThen _) )         //andThen
val ls14 = ls map ( fncs reduce (_ compose _) )         //compose

